                            } else if ($('td', row)[indice].textContent === "M") {
                                $('td', row).eq(indice).css('background-color',
                                    'rgb(220 38 38)');
                            }

                            $('td', row).eq(indice).css('text-align', 'center')
                            $('td', row).eq(indice).css('color', '#000')
